insert into PRODUCTORA(nombre,logo,mision,tipo,id_lugar) values ('Acopagro',BFILENAME('direc_imagenes_productora', 'acopagro_logo.png'),'Producir cacao orgánico con buen sabor y aroma, logrando la satisfacción de nuestros clientes','cacao',593);
insert into PRODUCTORA(nombre,logo,mision,tipo,id_lugar) values ('KKOReal',BFILENAME('direc_imagenes_productora', 'KKOReal_Logo.jpg'),'Mantenernos como una empresa líder en el procesamiento del cacao venezolano, con miras a lograr desarrollar innovadores productos que sustenten el crecimiento y la demanda de los mismos, dentro y fuera del país, garantizando el abastecimiento de los mercados, con responsabilidad social, el menor impacto al ambiente y considerando el capital humano.','cacao',58);
insert into PRODUCTORA(nombre,logo,mision,tipo,id_lugar) values ('Cofina',BFILENAME('direc_imagenes_productora', 'logo-cofinaOK2.png'),'Somos una empresa ecuatoriana dedicada a la manufactura y comercialización de cacao en grano y sus derivados cumpliendo altos estándares de calidad y seguridad alimentaria a través de un trabajo sostenible, socialmente responsable y amigable con el medio ambiente para asegurar confianza y satisfacción a nuestros clientes.','cacao',58);
insert into PRODUCTORA(nombre,logo,mision,tipo,id_lugar) values ('Friis Holm',BFILENAME('direc_imagenes_productora', 'Friis_Holm.jpg'),'Elabora sus barras a base de cacao especial micro-lote de naturaleza específica y de lugares muy específicos.','chocolate',45);
insert into PRODUCTORA(nombre,logo,mision,tipo,id_lugar) values ('Cacao Hunters',BFILENAME('direc_imagenes_productora', 'Cacao_Hunters.png'),'hacer el mejor chocolate del mundo y tener una conciencia social. Los agricultores afectados por la larga historia de conflicto armado, narcotráfico y pobreza en Colombia ahora pueden contribuir al creciente comercio del cacao.','chocolate',57);
insert into PRODUCTORA(nombre,logo,mision,tipo,id_lugar) values ('St Moritz',BFILENAME('direc_imagenes_productora', 'St_Moritz.png'),'Fabricar y distribuir chocolate de alta calidad que satisfaga plenamente a nuestros consumidores y clientes, optimizando la rentabilidad del negocio de nuestros accionistas, contando para ello con el respaldo y compromiso de nuestros proveedores así como la identificación con las metas de nuestra organización por parte del equipo laboral en un ambiente dinámico, y de alta motivación al logro.','chocolate',58);
insert into PRODUCTORA(nombre,logo,mision,tipo,id_lugar) values ('Milka',BFILENAME('direc_imagenes_productora', 'Milka.png'),'tratar las causas del trabajo infantil con un enfoque integral y centrado en las comunidades de agricultores. Nuestro planteamiento para erradicar el trabajo infantil tiene tres niveles: se centra en la prevención, el control y la erradicación, haciendo hincapié en la erradicación de las causas profundas del trabajo infantil. Ayudamos a los productores de cacao a operar de forma rentable.','chocolate',41);
insert into PRODUCTORA(nombre,logo,mision,tipo,id_lugar) values ('The Hershey Company',BFILENAME('direc_imagenes_productora', 'The_Hershey_Company.png'),'Ayudar a crear momentos significativos en la vida de las personas','chocolate',92110);
insert into PRODUCTORA(nombre,logo,mision,tipo,id_lugar) values ('Bean To Bar',BFILENAME('direc_imagenes_productora', 'Bean_To_Bar.jpg'),'hacer y vender chocolate real del que podemos obtener todos sus beneficios para la salud.','chocolate',215);
